mapper.update:
update users
set password = #{newPassword}
where user_id = #{userId}

update users
set user_status = #{status}
where user_id = #{userId}

update users
set money = money - #{deduct}
where user_id = #{userId}

mapper.select:
select *
from user_favorite
where user_id = #{userId}
  and project_id = #{projectId}

select user_id,account,password,nick_name,email,user_avatar,user_status
from users

where binary account = #{account}

select admin_id,account,password,email,nick_name
from admin_web_user

where binary account = #{account}
